// ==========================================
// 1. 严谨的清洗与提取函数 (DeepSeek 专用版)
// ==========================================
function safeParseJSON(input) {
    // 0. 空值检查
    if (!input) return { feeds: [] };
    if (typeof input === 'object' && input !== null) return input;

    let str = input.toString();

    // --- [新增 1] 针对 DeepSeek 的噪音清洗 ---
    // 必须先移除 <think> 标签，否则它们包含的字符会干扰 JSON 解析
    str = str.replace(/<think>[\s\S]*?<\/think>/gi, ""); 
    // 移除 Markdown 标记
    str = str.replace(/```json/gi, "").replace(/```/g, "");

    // --- [优化 2] 智能定位 JSON 起点 ---
    // 无论开头有多少废话，直接跳到第一个 "{" 或 "["
    const firstMatch = str.search(/[\{\[]/);
    if (firstMatch > -1) {
        str = str.substring(firstMatch);
    }
    // 移除末尾多余字符
    const lastMatch = str.search(/[\]\}][^\]\}]*$/);
    if (lastMatch > -1) {
        str = str.substring(0, lastMatch + 1);
    }

    // --- [修正 3] 移除原来的危险 replace，改为标准解析 ---
    try {
        const result = JSON.parse(str);
        // 如果解析出来还是字符串（DeepSeek 常见毛病），则进行二次解析
        if (typeof result === 'string') {
            try { return JSON.parse(result); } catch(e) {}
        }
        return result;
    } catch (e) {
        // 标准解析失败，才进入暴力模式
    }

    // --- [保留但优化] 暴力提取模式 ---
    // 仅在标准解析彻底失败时使用
    const validFeeds = [];

    // 这里的正则放松了条件，不再强制要求开头有 feeds
    // 直接在文本中寻找看起来像 Feed 对象的代码块
    let currentStr = str;

    // 如果能找到 "feeds": [...] 结构，先定位进去，缩小搜索范围
    const feedsArrayMatch = currentStr.match(/["']feeds["']\s*:\s*\[/i);
    if (feedsArrayMatch) {
        currentStr = currentStr.substring(feedsArrayMatch.index + feedsArrayMatch[0].length);
    }

    // 循环提取 { ... }
    while (true) {
        const startBrace = currentStr.indexOf('{');
        if (startBrace === -1) break;

        currentStr = currentStr.substring(startBrace);

        // 寻找匹配的闭合括号
        let balance = 0;
        let foundEnd = false;
        let endIndex = 0;

        // 使用更严谨的括号平衡算法，而不是简单的找 "}"
        for (let i = 0; i < currentStr.length; i++) {
            if (currentStr[i] === '{') balance++;
            else if (currentStr[i] === '}') balance--;

            if (balance === 0) {
                endIndex = i;
                foundEnd = true;
                break;
            }
        }

        if (foundEnd) {
            const jsonChunk = currentStr.substring(0, endIndex + 1);
            try {
                const obj = JSON.parse(jsonChunk);
                // 简单校验特征字段，避免提取到无用的小对象
                if (obj && (obj.id || obj.feedid || obj.xssecToken || obj.noteCard)) {
                    validFeeds.push(obj);
                }
            } catch (e) {
                // 解析失败忽略
            }
            currentStr = currentStr.substring(endIndex + 1);
        } else {
            // 括号不平衡，说明结构坏了，强制跳过当前字符
            currentStr = currentStr.substring(1);
        }
    }

    return { feeds: validFeeds };
}

// ==========================================
// 2. 主逻辑 (映射与输出)
// ==========================================
const results = [];

for (const item of items) {
    // 兼容取值
    let rawData = item.json.output || item.json.text || item.json.content || item.json;

    const cleanData = safeParseJSON(rawData);

    // 兼容根目录数组 或 feeds 字段数组
    let feeds = Array.isArray(cleanData) ? cleanData : (cleanData.feeds || []);
    if (!Array.isArray(feeds)) feeds = [];

    for (const feed of feeds) {
        const realId = feed.id || feed.feedid || "";
        if (!realId) continue; 

        // 安全解包
        const noteCard = feed.noteCard || {};
        const user = feed.user || {};
        const interact = feed.interactInfo || {};
        const cover = feed.cover || {};

        // 结果组装
        results.push({
            json: {
                ...feed, // 保留原始数据
                "id": realId, // 统一 ID
                "userid": user.userId || user.userid || "",
                "笔记标题": noteCard.displayTitle || noteCard.displaytitle || "无标题",
                "图片地址": cover.urlDefault || cover.urlPre || "",
                "点赞数": Number(interact.likedCount || 0),
                "收藏数": Number(interact.collectedCount || 0),
                "评论数": Number(interact.commentCount || 0),
                "分享数": Number(interact.sharedCount || 0),
                "博主账户名称": user.nickname || user.nickName || "",
                "笔记类型": noteCard.type || "normal",
                "笔记链接": `https://www.xiaohongshu.com/explore/${realId}`
            }
        });
    }
}

return results;

// 适配n8n Function节点：解析数据并输出目标字段
const results = [];

try {
    // 1. 获取输入数据（n8n中输入存在于items数组的json里）
    const inputItem = items[0]; // 取第一条输入数据
    const rawOutputStr = inputItem.json.output; // 拿到原始的output字符串

    // 2. 解析output中的JSON数据
    const bloggerData = JSON.parse(rawOutputStr);

    // 3. 处理字段（notes_total+1，匹配目标键名）
    const parsedData = {
        "笔记总数": (Number(bloggerData.notes_total) || 0) + 1, // 0起始计数转实际数
        "粉丝数": bloggerData.fans || "0",
        "获赞与收藏数": bloggerData.interaction || "0"
    };

    // 4. 构建n8n输出格式
    results.push({
        json: parsedData // 输出的json就是你要的三个字段
    });

} catch (err) {
    // 异常处理：输出错误信息
    results.push({
        json: {
            error: "解析失败",
            detail: err.message
        }
    });
}

// n8n Function节点必须返回results数组
return results;

