// 获取基础信息（标题、图片、ID等）
// 注意：确保节点名称与你画布上的完全一致
const baseData = $('所需全部字段提取').item.json;

// 获取 Agent 1 的结果（正文）
let content = "";
try {
    // 尝试解析 Agent 1 的输出，兼顾字符串和对象两种情况
    const agent1Raw = $('基于feed_id 正文提取').item.json.output;
    if (typeof agent1Raw === 'string') {
        // 简单的正则提取 JSON，防止由markdown包裹
        const match = agent1Raw.match(/\{[\s\S]*\}/);
        if (match) {
            content = JSON.parse(match[0]).content;
        } else {
            content = agent1Raw; // 兜底：直接用文本
        }
    } else {
        content = agent1Raw.content || "";
    }
} catch(e) { 
    content = ""; // 出错留空
}

// 获取 Agent 2 的结果（博主数据）
let blogger = { fans: 0, notes: 0, interaction: 0 };
try {
    const agent2Raw = $('根据userid获取博主 （笔记总数 粉丝 获赞与收藏数）').item.json.output;
    if (typeof agent2Raw === 'string') {
        const match = agent2Raw.match(/\{[\s\S]*\}/);
        if (match) {
            const parsed = JSON.parse(match[0]);
            blogger.fans = parsed.fans || 0;
            blogger.notes = parsed.notes_total || 0;
            blogger.interaction = parsed.interaction || 0;
        }
    } else {
        blogger.fans = agent2Raw.fans || 0;
        blogger.notes = agent2Raw.notes_total || 0;
        blogger.interaction = agent2Raw.interaction || 0;
    }
} catch(e) {}

// 输出最终合并的一条数据
return {
    json: {
        ...baseData, // 包含原来的 userid, feedid, 标题, 封面等
        "笔记正文": content,
        "粉丝数": Number(blogger.fans),
        "笔记总数": Number(blogger.notes),
        "获赞与收藏数": Number(blogger.interaction),
        "抓取状态": content ? "成功" : "部分缺失"
    }
};

// 获取 Agent 输出 (兼容 output 或 text 字段)
const agentOutput = item.json.output || item.json.text || "";

// 【关键】获取当前的重试次数 (默认0)
// 必须保留这个值，否则 Set 节点的计数会丢失
const currentRetry = item.json.retryCount || 0;

let content = "";
let status = "失败"; 

try {
    // 1. 深度清洗 (去除 DeepSeek 的 <think> 和 Markdown)
    let cleanText = "";
    if (typeof agentOutput === 'string') {
        cleanText = agentOutput
            .replace(/<think>[\s\S]*?<\/think>/gi, "") // 去思考标签
            .replace(/```json/gi, "") // 去 markdown
            .replace(/```/g, "")
            .trim();
    } else if (typeof agentOutput === 'object') {
        cleanText = agentOutput.content || JSON.stringify(agentOutput);
    }

    // 2. 尝试提取 JSON 中的 content
    if (cleanText.includes("{") && cleanText.includes("content")) {
        try {
            // 尝试提取 JSON 对象
            const match = cleanText.match(/\{[\s\S]*\}/);
            if (match) {
                const parsed = JSON.parse(match[0]);
                content = parsed.content || "";
            }
        } catch (e) {
            content = cleanText; // 解析失败降级为纯文本
        }
    } else {
        content = cleanText;
    }

    // 3. 判定逻辑 (排除 not found 和 空内容)
    if (!content || content.trim() === "" || content.length < 5) {
        status = "失败";
    } 
    else if (content.includes("not found") || content.includes("获取Feed详情失败")) {
        status = "失败";
        // 既然 not found，把内容清空，以免后续误用
        content = ""; 
    } 
    else {
        status = "成功";
    }

} catch (e) {
    status = "失败";
}

// 4. 输出结果
// 【重要】使用 ...item.json 保留原始数据(如 feedid, userid, retryCount)
return {
    json: {
        ...item.json, 
        "笔记正文": content,
        "抓取状态": status,
        "retryCount": currentRetry // 显式传递计数
    }
};

